<?php# Chat## ID# Time# Map# Account# Textinclude('mysql.php');include('functions.php');session_start();if($_SESSION['Name']){	if($_POST)	{		$Time = time();		$Session = session_id();		$Text = filter_var(trim(stripslashes($_POST['data'])), FILTER_SANITIZE_SPECIAL_CHARS);				if($Text)		{			$_SESSION['Active'] = $Time;					mysql_query("Insert into `Chat`							values('', '$Time', '{$_SESSION['Map']}', '{$_SESSION['AccountID']}', '$Text')");										mysql_query("Update `Players` set `Active`='$Time' where `Account`='{$_SESSION['AccountID']}'");		}	}}else{	echo "You need to <a href='#signin'>sign in</a> or <a href='#register'>register</a> to use the chat.";}echo "<table>";$Query = mysql_query("Select `Time`, `Account`, `Text`						from `Chat`						where `Map`='{$_SESSION['Map']}'						order by `ID` desc						limit 10");						while(list($Time, $AccountID, $Text) = mysql_fetch_array($Query)){	$AccountQuery = mysql_query("Select `Name`									from `Accounts`									where `ID`='$AccountID'");										list($Name) = mysql_fetch_array($AccountQuery);		$Time = FormatTime($Time);	$Text = FormatURL($Text);		echo "<tr><td class='time'> $Time </td><td> <b>$Name</b> </td><td> $Text </td></tr>";}echo "</table>";echo "<a href='#chat-history'>View Chat History</a>";?>