<?php $Search = filter_var(stripslashes($_GET['q']), FILTER_SANITIZE_SPECIAL_CHARS); ?><form>	<input type='text' name='q' value='<?php echo $Search ?>'>	<input type='submit' value='Search'></form><?phpinclude_once('mysql.php');include_once('functions.php');include_once('paginate.php');if($Search){	$RecentQuery = "Select `ID`, `Time`, `Caption`, `Permalink` from `Images` where match(`Caption`) against('$Search')";	list($Data, $Links) = Paginate($RecentQuery, 25, $_GET['page'], $_SERVER['QUERY_STRING'], 'redirect');		if($Data)	{		echo "<center>$Links</center>";		echo "<table>";		foreach($Data as $Result)		{		list($ID, $Time, $Caption, $Permalink) = $Result;		$Time = FormatTime($Time);		echo "<tr><td class='time'>$Time</td> <td><a href='/view/$ID/$Permalink/'>$Caption</a></a></td></tr>\n";		}		echo "</table>";		echo "<center>$Links</center>";	}		$Caption = "Searching for $Search";	$Title = "Searching for $Search";}else{	$Caption = "Search !!";	$Title = $Caption;	$Tags = DisplayAllTags();}?>